import React, { useCallback, useState, useEffect, useRef } from "react";

function EventForm() {

    const [error, setError] = useState('')
    const [theaters, setTheaters] = useState([])
    const [movies, setMovies] = useState([])

    const movieIdRef = useRef('')
    const nameRef = useRef('')

    const getTheaters = useCallback(async () => {
        try {
            const response = await fetch('getTheaters');
            if (!response.ok) {
              throw new Error('Something went wrong!');
            }
            const data = await response.json();
            setTheaters(data);
          } catch (error) {
            setError("There's an issue with the server.")
          }
      }); 

      const loadMovies = useCallback(async () => {
        try {
            const requestOptions = {
                method: "POST",
                headers: { "Content-Type": "application/json" , token: ''},
                body: JSON.stringify(
                {
                  "name": nameRef.current.value
                })
              };
            const response = await fetch('movie/filter', requestOptions);
            if (!response.ok) {
              throw new Error('Something went wrong!');
            }
            const data = await response.json();
            setMovies(data);
          } catch (error) {
            setError("There's an issue with the server.")
          }
      }); 

    useEffect(() => {
        getTheaters();
      }, [])

    return ( 
        <form>
            <input list="browsers" name="myBrowser" />
            <datalist id="browsers">
                <option value="Chrome"/>
                <option value="Firefox"/>
                <option value="Internet Explorer"/>
                <option value="Opera"/>
                <option value="Safari"/>
                <option value="Microsoft Edge"/>
            </datalist>
            <div className="error">{error}</div>
            <br/>
            Movie name
            <br/>
            <input type='text' onBlur={loadMovies} ref={nameRef}/>
            <select ref={movieIdRef}>
                {movies.map(movie => {return(
                    <option key={movie.id} value={movie.id}>{movie.name}</option>
                )})}
            </select>
            
            <br/><br/>
            Starting time
            <br/>
            <input type="datetime-local" />
            <br/><br/>
            Theater
            <br/>
            <select>
                {theaters.map(theater => {return(
                    <option key={theater.id} value={theater.id}>#{theater.theaterNumber} ({theater.seats} seats)</option>
                )})}
            </select>

        </form>
     );
}

export default EventForm;
