import React, { useCallback, useState, useEffect, useRef } from "react";

function EventForm() {

    const [error, setError] = useState('')
    const [theaters, setTheaters] = useState([])
    const [movies, setMovies] = useState([])

    const movieNameRef = useRef('')
    const nameRef = useRef('')

    const getTheaters = useCallback(async () => {
        try {
            const response = await fetch('getTheaters');
            if (!response.ok) {
              throw new Error('Something went wrong!');
            }
            const data = await response.json();
            setTheaters(data);
          } catch (error) {
            setError("There's an issue with the server.")
          }
    }); 

      const getMovies = useCallback(async () => {
        try {
            
            const response = await fetch('movie/all');
            if (!response.ok) {
              throw new Error('Something went wrong!');
            }
            const data = await response.json();
            setMovies(data);
          } catch (error) {
            setError("There's an issue with the server.")
          }
      }); 

      const addEvent = e =>{
        e.preventDefault();
        console.log(movieNameRef.current.value);
        try {
            const movie = movies.filter(movie => movie.name==movieNameRef.current.value)[0];
            console.log(movie);
            const event = {
                movie: {
                    id: movie.id
                }
            }
            console.log(event);
          }
        catch (error) {
            error
        }
    }
        


    useEffect(() => {
        getTheaters();
        getMovies()
      }, [])

    return ( 
        <form>
            <div className="error">{error}</div>
            <br/>
            Movie name
            <br/>
            <input list="movielist" ref={movieNameRef}/>
            <datalist id="movielist">
                {movies.map(movie => {return(
                        <option id={movie.id} key={movie.id} value={movie.name}/>
                    )})}
            </datalist>
            <br/><br/>
            Starting time
            <br/>
            <input type="datetime-local" />
            <br/><br/>
            Theater
            <br/>
            <select>
                {theaters.map(theater => {return(
                    <option key={theater.id} value={theater.id}>#{theater.theaterNumber} ({theater.seats} seats)</option>
                )})}
            </select>
            <br/><br/>
            <button onClick={addEvent} > Submit </button>
        </form>
     );
}

export default EventForm;
