import React, {useRef} from "react";
import './MoviesFilters.css'

function MoviesFilters() {
    return (  
        <React.Fragment>
        
        <h3>Filters</h3>

        <div className="moviesfilters-container">
            
            <input type='text' placeholder={<a color="red">Movie name</a>}/>
            <select>
                <option value='0'>Horror</option>
                <option value='1'>Comedy</option>
                <option value='2'>SCI_FI</option>
                <option value='3'>Action</option>
                <option value='4'>Adventure</option>
            </select>

        </div>

        </React.Fragment>
    );
}

export default MoviesFilters;
