import React from "react";
import './EventCard.css'


function EventCard(props) {

    let datetime = props.event.startTime
    datetime = new Date(datetime)
    const time = datetime.toLocaleTimeString();
    const date = datetime.toLocaleDateString();

    const monthNames = ["January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"];

    date = date.split('.');
    const day = date[0]
    const month = date[1]
    const year = date[2]
    console.log(day + monthNames[month] + year);


    

    function onPurchase(event) {
        const eventId = event.target.id;

        console.log("PT2H11M");
    }

    return (  
        <div className="eventcard-container">

            <img src={props.event.movie.image}/>

            <div className="name">
            {props.event.movie.name}
            </div>
            <div className="date">{date}</div>
            <section className="time">
                <div>{time}</div>
                <div>{props.event.movie.length}</div>
            </section>
            <div className="purchase">
                <button id='example' onClick={onPurchase}>Purchase</button>
            </div>
            
            
            
            
            

        </div>
    );
}

export default EventCard;
