import { NavLink } from 'react-router-dom';
import React, { useState } from 'react';
import './NavBar.css'

function NavBar() {

    const [activeTab, setActiveTab] = useState('0')

    function toggleTabs(event) {
        if(event.target.id !== activeTab){
            setActiveTab(event.target.id)
        }
    }

    function onTabClick(event) {
        const tabs = document.getElementsByName('tabs')
        tabs.forEach(element => {
        element.className = '';            
        });

        const pressedTab = document.getElementById(event.target.id)
        pressedTab.className = 'active';
    }
    

    return ( 
        <div id='navbar' className='navbar-container'>

                <NavLink to='/'>
                <button id='0' name='tabs' className='active' onClick={onTabClick}> <img  id='0' src='favicon.ico'/> </button>
                </NavLink>
                <NavLink to='/movies'>
                <button id='1' name='tabs' onClick={onTabClick}>Movies</button>
                </NavLink>
                <NavLink to='/events'>
                <button id='2' name='tabs' onClick={onTabClick}>Events</button>
                </NavLink>
                <NavLink to='/admin'>
                <button id='3' name='tabs' onClick={onTabClick}>Admin</button>
                </NavLink>
        
        </div>
     );
}

export default NavBar;
