import { NavLink } from 'react-router-dom';
import React from 'react';
import './NavBar.css'
import { useState } from 'react';

function NavBar() {

    function onTabClick(event) {
        console.log(event.target.id)

        const tabs = document.getElementsByName('tabs')
        console.log(tabs);
        tabs.forEach(element => {
        element.className = '';            
        });
        console.log(tabs);
        const pressedTab = document.getElementById(event.target.id)
        pressedTab.className = 'active';
    }
    

    return ( 
        <div id='navbar' className='navbar'>

                <NavLink to='/'>
                <button id='home' name='tabs' className='active' onClick={onTabClick}> <img  id='home' src='favicon.ico'/> </button>
                </NavLink>
                <NavLink to='/tab1'>
                <button id='1' name='tabs' onClick={onTabClick}>Movies</button>
                </NavLink>
                <NavLink to='/tab2'>
                <button id='2' name='tabs' onClick={onTabClick}>Events</button>
                </NavLink>
                <NavLink to='/tab3'>
                <button id='3' name='tabs' onClick={onTabClick}>Admin</button>
                </NavLink>
        
        </div>
     );
}

export default NavBar;
