import React from "react";
import './MoviesList.css';
import MovieCard from '../../../cards/moviecard/MovieCard'

function MoviesList() {

    const movies = [{"id":1,"name":"Harry Potter","genre":"ADVENTURE","desc":"The sorcerer's stone","length":"PT2H11M","image":"https://media.harrypotterfanzone.com/philosophers-stone-20-years-of-movie-magic-harry-poster.jpg"},{"id":3,"name":"Black Panther","genre":"ADVENTURE","desc":"Long live the king","length":"PT2H11M10S","image":"https://www.washingtonpost.com/graphics/2019/entertainment/oscar-nominees-movie-poster-design/img/black-panther-web.jpg"},{"id":4,"name":"Star balls","genre":"HORROR","desc":"The last jedi","length":"PT59M20S","image":"https://www.digitalartsonline.co.uk/cmsdata/slideshow/3662115/star-wars-last-jedi-poster.jpg"}]

    return ( 
        <React.Fragment>

            <h3>Movies list</h3>

            <div className="movieslist-container">

                {movies.map(element => {
                    return(
                    <MovieCard movie={element}/>
                    )
                })}

            </div>

        </React.Fragment>
     );
}

export default MoviesList;
