import React, {useRef, useCallback, useState} from "react";
import '../../Filters.css'

function MoviesFilters(props) {

    const [genres, setGenres] = useState([])
    const nameFilterRef = useRef('')
    const genreFilterRef = useRef('')
    const fetchGenres = useCallback(async () => {
        try {
            const response = await fetch('getGenres');
            if (!response.ok) {
              throw new Error('Something went wrong!');
            }
            const data = await response.json();
            setGenres(data);
          } catch (error) {}
      },[]);
    



    const fetchMoviesHandler = useCallback(async () => {
        try {
          const response = await fetch('movie/all');
          if (!response.ok) {
            throw new Error('Something went wrong!');
          }
          const data = await response.json();
          props.setMovies(data);
        } catch (error) {}

        const name = nameFilterRef.current.value;
        const genre = genreFilterRef.current.value;

        console.log('name: ' + name + '\ngenre: ' + genre);

    }, []);

    function onSearch(event) {



    }

    return (  
        <React.Fragment>

            <h3>Filters</h3>

            <div className="filters-container">
                
                <filters>
                <input type='text' placeholder='Movie name' ref={nameFilterRef}/>

                <select ref={genreFilterRef}>
                <option value='' disabled selected hidden>Genre</option>
                <option value='' >All</option>
                {genres.map(element => {
                    <option value={element}>{element}</option>
                })}
                <option value='0'>Horror</option>
                <option value='1'>Comedy</option>
                <option value='2'>SCI_FI</option>
                <option value='3'>Action</option>
                <option value='4'>Adventure</option>
                <option value='5'>Romance</option>
                </select> 
                </filters>

                <button onClick={fetchMoviesHandler}>Search</button>

            </div>

            

        </React.Fragment>

    );
}

export default MoviesFilters;
