import React from "react";
import './EventsList.css';

import EventCard from '../../../cards/eventcard/EventCard'

    const events = [
    {"id":10,
    "movie":{"id":1,"name":"Harry Potter","genre":"ADVENTURE","desc":"harry potter movie stuff","length":"PT2H11M40S","image":"https://media.harrypotterfanzone.com/philosophers-stone-20-years-of-movie-magic-harry-poster.jpg"},
    "theater":{"id":8,"theaterNumber":1,"seats":10},
    "startTime":"2022-07-14T10:00:00",
    "endTime":"2022-07-14T11:00:00",
    "tickets_sold":0},
    {"id":11,
    "movie":{"id":3,"name":"Black Panther","genre":"ADVENTURE","desc":"Long live the king","length":"PT2H11M10S","image":"https://www.washingtonpost.com/graphics/2019/entertainment/oscar-nominees-movie-poster-design/img/black-panther-web.jpg"},
    "theater":{"id":8,"theaterNumber":1,"seats":10},
    "startTime":"2022-07-14T12:00:00",
    "endTime":"2022-07-14T13:30:00",
    "tickets_sold":0}
    ]

function EventsList(props) {

    return (  
        <React.Fragment>

            <h3>Events List</h3>
            <div className="eventslist-container">

                {props.events.map(element => {
                    return(
                        <EventCard key={element.id} event={element}/>
                    )})}
                
            </div>

        </React.Fragment>
    );
}

export default EventsList;
