import React, {useState} from "react";

function Store() {
    
    const [name, setName] = useState('Iftach')
    const [age, setAge] = useState(24)

    const state ={
        name: name,
        age: age,
    }

    const actions ={
        setName: setName,
        setAge: setAge
    }
    
    return ( state, functions );
}

export default Store;
