import React from "react";
import { useState } from "react";


function SubTabs(props) {

    const [activeBar, setActiveBar] = useState(tabs[0])

    const toggleTabs = event => {
        setActiveBar(tabs[event.target.id])
        
    }
    
    const tabs = [
        {
          key: '0',
          title: 'tab1',
          content: 'content 1'
        },
        {
          key: '1',
          title: 'tab2',
          content: 'content 2'
        },
        {
          key: '2',
          title: 'tab3',
          content: 'content 3'
        }
    ]
    

    
    return (  
        <React.Fragment>
        <div>
            {tabs.map(tab => {return(
                <button id={tab.key} key={tab.key} className={tab.key===activeBar ? 'active' : ''}  onClick={toggleTabs}>{tab.title}</button>
            )})}
        </div>
        <div>content: {tabs[activeBar].content}</div>
        </React.Fragment>
    );
}

export default SubTabs;
